import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const RestaurantListFlex = styled.div`
  display: grid;
  gap: 16px; /* Adjust the gap between cards */
  justify-content: center;
  align-items: center;
  border: 2px solid red;

/* Media query for mid-sized screens (2 cards per row) */
@media (max-width: 768px) {
  .restaurant-list {
    grid-template-columns: repeat(2, 1fr);
  }
}

`;

const RestaurantCard = styled.div`

height:200px; /*this need to change  */


  background-color: #fff;
  border: 1px solid #eaeaea;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.1);
  padding: 16px;
  margin-bottom: 16px;
  border-radius: 14px;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center center;
  background-image: ${(props) => `url(${require(`./assets/restaurants/${props.logo}`)})`};
  background-color: #E0E0E0;
  background-size: cover;
  background-position: center;
  background-blend-mode: multiply;
  color: #fff;


/* Use absolute URL path */

/* Media Queries for Responsive Card Size */
  @media (max-width: 768px) {


   /* in this media query size i will need to change the grid */
  }

  @media (min-width: 769px) and (max-width: 1024px) {

  }

  /* Add more media queries as needed for other screen sizes */
`;

const RestaurantListGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr); /* Display 3 columns */
  gap: 16px; /* Adjust the gap between cards */
  justify-content: center;
  align-items: center;
`;

const Header = styled.div`
background-color: rgba(0, 0, 0, 0.35);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
`


const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/restaurants')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => setRestaurants(data))
      .catch((error) => console.error('Fetch error: ', error));
  }, []);

  return (
    <div className='App'>
      <h2>Restaurant List</h2>
      <RestaurantListFlex>
        <RestaurantListGrid >
        {restaurants.map((restaurant) => (
          <RestaurantCard key={restaurant.id} logo={restaurant.logo}>
            <div style={{border:'2px solid red',width:'100%',height:'100%',padding:0}}>
                <p>{restaurant.Name} - {restaurant.rating} - {restaurant.logo}</p>
             </div>

          </RestaurantCard>

        ))}
        </RestaurantListGrid>
      </RestaurantListFlex>

    </div>
  );
};


export default RestaurantList;
