import React, { useState, useEffect } from 'react';


export const Search = () => {

   const [activeFilters, setActiveFilters] = useState([]);

    useEffect(()=>{
        alert(activeFilters)
    },[])


   const handleFilter = (filterCriteria) => {
  // Check if the filter is already active
  if (activeFilters.includes(filterCriteria)) {
    // If so, remove it to "deactivate" the filter
    setActiveFilters(activeFilters.filter((filter) => filter !== filterCriteria));
  } else {
    // If not, add it to the active filters
    setActiveFilters([...activeFilters, filterCriteria]);
  }
};

  return (
    <div>
        <button onClick={(a)=>setActiveFilters(a.value)} value="hi there"></button>
    </div>
  )
}
