import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import FilterButtons from './components/FilterButtons'; // Import the FilterButtons component
import SearchBar from './components/SearchBar'; // Import the SearchBar component



const RestaurantCard = styled.div`

height:200px; /*this need to change  */


  background-color: #fff;
  border: 1px solid #eaeaea;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.1);
  padding: 16px;
  margin-bottom: 16px;
  border-radius: 14px;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center center;
  background-image: ${(props) => `url(${require(`./assets/restaurants/${props.logo}`)})`};
  background-color: #E0E0E0;
  background-size: cover;

  background-blend-mode: multiply;
  color: #fff;


/* Use absolute URL path */

/* Media Queries for Responsive Card Size */
  @media (max-width: 768px) {


   /* in this media query size i will need to change the grid */
  }

  @media (min-width: 769px) and (max-width: 1024px) {

  }

  /* Add more media queries as needed for other screen sizes */
`;

const RestaurantListGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr); /* Display 3 columns */
  gap: 16px; /* Adjust the gap between cards */
  justify-content: center;
  align-items: center;
`;

const Header = styled.div`
background-color: rgba(0, 0, 0, 0.35);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
`


const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);
   const [searchQuery, setSearchQuery] = useState('');
  const [activeFilters, setActiveFilters] = useState([]);


    const handleFilter = (filter) => {
      console.log(activeFilters);
    // Check if the filter is already active
    if (activeFilters.includes(filter)) {
      // Deactivate the filter by removing it
      setActiveFilters(activeFilters.filter((active) => active !== filter));
    } else {
      // Activate the filter by adding it
      setActiveFilters([...activeFilters, filter]);
    }

// this is the logic for returning the matching array
    //  const matchesSearch = restaurant.Name.toLowerCase().includes(searchQuery.toLowerCase());

  // Apply filter logic based on activeFilters
  const matchesFilters = activeFilters.length === 0 || activeFilters.includes(restaurant.cuisine);

  // Return true for items that match both search and filters
  // return matchesSearch && matchesFilters; this only when search bar is implemented
    return matchesFilters;


  };


  useEffect(() => {

    fetch('http://localhost:3001/restaurants')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => setRestaurants(data))
      .catch((error) => console.error('Fetch error: ', error));
  }, []);

  return (
    <div className='App'>
      <h2>Restaurant List</h2>
        <FilterButtons activeFilters={activeFilters} onFilter={handleFilter} />
        <RestaurantListGrid >
        {restaurants.map((restaurant) => (
          <RestaurantCard key={restaurant.id} logo={restaurant.logo}>
            <div style={{border:'2px solid red',width:'100%',height:'100%',padding:0}}>
                <p>{restaurant.Name} - {restaurant.rating} - {restaurant.logo}</p>
             </div>

          </RestaurantCard>

        ))}
        </RestaurantListGrid>


    </div>
  );
};


export default RestaurantList;
