import React, { useState, useEffect } from 'react';
import styled from 'styled-components';


const RestaurantListFlex =styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 16px; /* Adjust spacing between cards */
  justify-content:center;
align-items:center;
border:2px solid red;
`;


const RestaurantCard = styled.div`
flex: 0 0 calc(33.33% - 8px); /* Three equal-width columns with spacing */
  /* 33.33% represents the width of each column, and 8px is half of the gap value */

 background-color: #fff;
  border: 1px solid #eaeaea;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.1);
  padding: 16px;
  margin-bottom: 16px;
  border-radius: 4px;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center center;
  background-image: ${(props) => `url(./assets/restaurants/${props.logo})`}; /* Set the background image dynamically */
`;


const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);

 useEffect(() => {
  fetch('http://localhost:3001/restaurants')
    .then((response) => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then((data) => setRestaurants(data))
    .catch((error) => console.error('Fetch error: ', error));
}, []);

  return (
    <div className='App'>
    <h2>Restaurant List</h2>
      <RestaurantListFlex>

      {restaurants.map((restaurant) => (
        <RestaurantCard key={restaurant.id} imageUrl={restaurant.logo}>

          <p>{restaurant.Name} - {restaurant.rating}-{restaurant.logo}</p>


        </RestaurantCard>
      ))}
    </RestaurantListFlex>
    </div>
  );
}

export default RestaurantList;
